import com.impaq.arena.api.Game; import com.impaq.arena.api.PlayerStrategy; public class MyStrategy implements PlayerStrategy {        int round;        @Override    public void playRound(Game game) {        if (round % 2 == 0) {            game.recruitBuilders();        } else {            game.recruitWizards();        }        game.buildMyCastle();        game.attackEnemyBuilders();        round++;    }}